importPackage(Packages.com.vordel.trace);

function invoke(msg) 
{ 
	var mockDetails = msg.get("apimgmt.apiproxy").get("tags").get("mock");
	if(mockDetails == null) {
		Trace.info("No global API-Mocking configured.");
	} else {
		mockDetails = String(mockDetails.get(0));
		Trace.info("API-Global mock-settings: " + mockDetails);
	}
	var methodMockDetails = msg.get("methodMockDetails");
	// Method-Mocking overrules global API-Mocking
	if(methodMockDetails == null) {
		Trace.info("No Mocking defined on operation");
	} else {
		mockDetails = String(methodMockDetails);
		Trace.info("Using mock-settings from operation: " + mockDetails);
	}
	if (mockDetails == null) {
		return true;
	}

	var isMocked = mockDetails.split("|")[0];
	var mockURL = mockDetails.split("|")[1];

	msg.put("isMocked", isMocked);
	msg.put("mockURL", mockURL);

	Trace.info("Using mockURL: " + mockURL + "; isMocked: " + isMocked);
	return true;         
}

importPackage(Packages.com.vordel.trace);

// Unfortunately we have to use a loop
// If performance is an issue, you may use a cache to store methodMockDetails

function invoke(msg) 
{ 
	var allMethods = msg.get("apimgmt.apiproxy").get("apis");
	var methodId = msg.get("api.method.id");
	var allOperations;
	var foundMethodId;
	var methodMockDetails;
	var found = false;
	for (var i = 0; i < allMethods.size(); i++) {
		allOperations = allMethods.get(i).get("operations");
		for (var z = 0; z < allOperations.size(); z++) {
			foundMethodId = allOperations.get(z).get("id");
			if(foundMethodId == methodId) {
				methodMockDetails =  allOperations.get(z).get("tags").get("mock");
				if(methodMockDetails == null) {
					Trace.info("No API-Method mock-settings configured");
					break;
				}
				methodMockDetails = String(methodMockDetails.get(0));
				Trace.info("Configured operation mock-settings: " + methodMockDetails);
				msg.put("methodMockDetails", methodMockDetails);
				found = true;
				break;
			}
		}
		if(found) break;
	}
	return true;
}
